            "aya_text": "\u0627\u064e\u0644\u0634\u0651\u064e\u0647\u0652\u0631\u064f \u0627\u0644\u0652\u062d\u064e\u0631\u064e\u0627\u0645\u064f \u0628\u0650\u0627\u0644\u0634\u0651\u064e\u0647\u0652\u0631\u0650 \u0627\u0644\u0652\u062d\u064e\u0631\u064e\u0627\u0645\u0650 \u0648\u064e\u0627\u0644\u0652\u062d\u064f\u0631\u064f\u0645\u0670\u062a\u064f \u0642\u0650\u0635\u064e\u0627\u0635\u064c\u06d7 \u0641\u064e\u0645\u064e\u0646\u0650 \u0627\u0639\u0652\u062a\u064e\u062f\u0670\u0649 \u0639\u064e\u0644\u064e\u064a\u0652\u0643\u064f\u0645\u0652 \u0641\u064e\u0627\u0639\u0652\u062a\u064e\u062f\u064f\u0648\u0652\u0627 \u0639\u064e\u0644\u064e\u064a\u0652\u0647\u0650 \u0628\u0650\u0645\u0650\u062b\u0652\u0644\u0650 \u0645\u064e\u0627 \u0627\u0639\u0652\u062a\u064e\u062f\u0670\u0649 \u0639\u064e\u0644\u064e\u064a\u0652\u0643\u064f\u0645\u0652 \u06d6 \u0648\u064e\u0627\u062a\u0651\u064e\u0642\u064f\u0648\u0627 \u0627\u0644\u0644\u0651\u0670\u0647\u064e \u0648\u064e\u0627\u0639\u0652\u0644\u064e\u0645\u064f\u0648\u0652\u0653\u0627 \u0627\u064e\u0646\u0651\u064e \u0627\u0644\u0644\u0651\u0670\u0647\u064e \u0645\u064e\u0639\u064e \u0627\u0644\u0652\u0645\u064f\u062a\u0651\u064e\u0642\u0650\u064a\u0652\u0646\u064e ",
            "sura_id": 2,
