        },
        {
            "aya_id": 233,
            "aya_number": 226,
            "aya_text": "\u0644\u0650\u0644\u0651\u064e\u0630\u0650\u064a\u0652\u0646\u064e \u064a\u064f\u0624\u0652\u0644\u064f\u0648\u0652\u0646\u064e \u0645\u0650\u0646\u0652 \u0646\u0650\u0651\u0633\u064e\u0627\u06e4\u0649\u0655\u0650\u0647\u0650\u0645\u0652 \u062a\u064e\u0631\u064e\u0628\u0651\u064f\u0635\u064f \u0627\u064e\u0631\u0652\u0628\u064e\u0639\u064e\u0629\u0650 \u0627\u064e\u0634\u0652\u0647\u064f\u0631\u064d\u06da \u0641\u064e\u0627\u0650\u0646\u0652 \u0641\u064e\u0627\u06e4\u0621\u064f\u0648\u0652 \u0641\u064e\u0627\u0650\u0646\u0651\u064e \u0627\u0644\u0644\u0651\u0670\u0647\u064e \u063a\u064e\u0641\u064f\u0648\u0652\u0631\u064c \u0631\u0651\u064e\u062d\u0650\u064a\u0652\u0645\u064c ",
            "sura_id": 2,
