        },
        {
            "aya_id": 1106,
            "aya_number": 152,
            "aya_text": "\u0627\u0650\u0646\u0651\u064e \u0627\u0644\u0651\u064e\u0630\u0650\u064a\u0652\u0646\u064e \u0627\u062a\u0651\u064e\u062e\u064e\u0630\u064f\u0648\u0627 \u0627\u0644\u0652\u0639\u0650\u062c\u0652\u0644\u064e \u0633\u064e\u064a\u064e\u0646\u064e\u0627\u0644\u064f\u0647\u064f\u0645\u0652 \u063a\u064e\u0636\u064e\u0628\u064c \u0645\u0651\u0650\u0646\u0652 \u0631\u0651\u064e\u0628\u0651\u0650\u0647\u0650\u0645\u0652 \u0648\u064e\u0630\u0650\u0644\u0651\u064e\u0629\u064c \u0641\u0650\u0649 \u0627\u0644\u0652\u062d\u064e\u064a\u0670\u0648\u0629\u0650 \u0627\u0644\u062f\u0651\u064f\u0646\u0652\u064a\u064e\u0627\u06d7 \u0648\u064e\u0643\u064e\u0630\u0670\u0644\u0650\u0643\u064e \u0646\u064e\u062c\u0652\u0632\u0650\u0649 \u0627\u0644\u0652\u0645\u064f\u0641\u0652\u062a\u064e\u0631\u0650\u064a\u0652\u0646\u064e",
