            "aya_text": "\u0648\u064e\u0627\u0644\u0651\u064e\u0630\u0650\u064a\u0652\u0646\u064e \u0639\u064e\u0645\u0650\u0644\u064f\u0648\u0627 \u0627\u0644\u0633\u0651\u064e\u064a\u0651\u0650\u0627\u0670\u062a\u0650 \u062b\u064f\u0645\u0651\u064e \u062a\u064e\u0627\u0628\u064f\u0648\u0652\u0627 \u0645\u0650\u0646\u0652\u06e2 \u0628\u064e\u0639\u0652\u062f\u0650\u0647\u064e\u0627 \u0648\u064e\u0627\u0670\u0645\u064e\u0646\u064f\u0648\u0652\u0653\u0627 \u0627\u0650\u0646\u0651\u064e \u0631\u064e\u0628\u0651\u064e\u0643\u064e \u0645\u0650\u0646\u0652\u06e2 \u0628\u064e\u0639\u0652\u062f\u0650\u0647\u064e\u0627 \u0644\u064e\u063a\u064e\u0641\u064f\u0648\u0652\u0631\u064c \u0631\u0651\u064e\u062d\u0650\u064a\u0652\u0645\u064c",
            "sura_id": 7,
