        },
        {
            "aya_id": 1121,
            "aya_number": 167,
            "aya_text": "\u0648\u064e\u0627\u0650\u0630\u0652 \u062a\u064e\u0627\u064e\u0630\u0651\u064e\u0646\u064e \u0631\u064e\u0628\u0651\u064f\u0643\u064e \u0644\u064e\u064a\u064e\u0628\u0652\u0639\u064e\u062b\u064e\u0646\u0651\u064e \u0639\u064e\u0644\u064e\u064a\u0652\u0647\u0650\u0645\u0652 \u0627\u0650\u0644\u0670\u0649 \u064a\u064e\u0648\u0652\u0645\u0650 \u0627\u0644\u0652\u0642\u0650\u064a\u0670\u0645\u064e\u0629\u0650 \u0645\u064e\u0646\u0652 \u064a\u0651\u064e\u0633\u064f\u0648\u0652\u0645\u064f\u0647\u064f\u0645\u0652 \u0633\u064f\u0648\u0652\u06e4\u0621\u064e \u0627\u0644\u0652\u0639\u064e\u0630\u064e\u0627\u0628\u0650\u06d7 \u0627\u0650\u0646\u0651\u064e \u0631\u064e\u0628\u0651\u064e\u0643\u064e \u0644\u064e\u0633\u064e\u0631\u0650\u064a\u0652\u0639\u064f \u0627\u0644\u0652\u0639\u0650\u0642\u064e\u0627\u0628\u0650\u06d6 \u0648\u064e\u0627\u0650\u0646\u0651\u064e\u0647\u0657 \u0644\u064e\u063a\u064e\u0641\u064f\u0648\u0652\u0631\u064c \u0631\u0651\u064e\u062d\u0650\u064a\u0652\u0645\u064c",
            "sura_id": 7,
            "juz_id": 9,
